$(document).ready(function(){$("#button1").click(function(){$.ajax({url:"libs/php/icao.php",type:"GET",dataType:"json",data:{airportCode:$("#airportCode").val()},success:function(t){"ok"===t.status.name?$("#result1").html(`<table>\n                            <tr><td>Clouds:</td><td>${t.data.clouds}</td></tr>\n                            <tr><td>Humidity:</td><td>${t.data.humidity}</td></tr>\n                            <tr><td>Wind Speed:</td><td>${t.data.windSpeed}</td></tr>\n                            <tr><td>Wind Direction:</td><td>${t.data.windDirection}</td></tr>\n                        </table>`):$("#result1").html("Error: "+t.status.description)},error:function(t,d,n){$("#result1").html("Error retrieving data: "+d+" - "+n)}})}),$("#button2").click(function(){$.ajax({url:"libs/php/timezone.php",type:"GET",dataType:"json",data:{lat:$("#lat").val(),lng:$("#lng").val()},success:function(t){"ok"===t.status.name?$("#result2").html(`<table>\n                            <tr><td>Country:</td><td>${t.data.timezoneId}</td></tr>\n                            <tr><td>Timezone:</td><td>${t.data.time}</td></tr>\n                        </table>`):$("#result2").html("Error: "+t.status.description)},error:function(t,d,n){$("#result2").html("Error retrieving data: "+d+" - "+n)}})}),$("#button3").click(function(){$.ajax({url:"libs/php/earthquakes.php",type:"GET",dataType:"json",data:{north:$("#north").val(),south:$("#south").val(),east:$("#east").val(),west:$("#west").val()},success:function(t){if("ok"===t.status.name){let d=t.data.earthquakes,n="<table>\n                                    <thead>\n                                        <tr>\n                                            <th>Datetime</th>\n                                            <th>Depth</th>\n                                            <th>Longitude</th>\n                                            <th>Source</th>\n                                            <th>EQID</th>\n                                            <th>Magnitude</th>\n                                            <th>Latitude</th>\n                                        </tr>\n                                    </thead>\n                                    <tbody>";d.forEach(t=>{n+=`<tr>\n                                    <td>${t.datetime}</td>\n                                    <td>${t.depth}</td>\n                                    <td>${t.lng}</td>\n                                    <td>${t.src}</td>\n                                    <td>${t.eqid}</td>\n                                    <td>${t.magnitude}</td>\n                                    <td>${t.lat}</td>\n                                  </tr>`}),n+="</tbody></table>",$("#result3").html(n)}else $("#result3").html("Error: "+t.status.description)},error:function(t,d,n){$("#result3").html("Error retrieving data: "+d+" - "+n)}})})});